definition.script:
pipeline {
    agent any
    tools{
        maven 'M3'
    }

    stages {
        stage('Build Maven') {
            steps {
                checkout([$class: 'GitSCM', branches: [[name: '*/master']], 
                    userRemoteConfigs: [[url: 'https://github.com/KiranJinka45/docker_helloworld_app.git']]
                ])
                sh 'mvn -f ./pom.xml clean install'
            }
        }

        stage('Build Docker Image') {
            steps {
                script {
                    sh 'docker build -t kiranjinka/docker-helloworld-app .'
                }
            }
        }
    }
}
